mapper.insert:
INSERT INTO payment_info (
    order_id, user_id, order_no, transaction_id, payment_type, 
    total_amount, subject, payment_status, callback_time, callback_content, create_time, update_time
) VALUES (
    #{orderId}, #{userId}, #{orderNo}, #{thirdPartyPaymentNo}, #{paymentMethod}, 
    #{amount}, #{paymentNo}, #{status}, #{paymentTime}, #{callbackContent}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE payment_info
order_id = #{orderId}, user_id = #{userId}, order_no = #{orderNo}, subject = #{paymentNo}, transaction_id = #{thirdPartyPaymentNo}, payment_type = #{paymentMethod}, total_amount = #{amount}, payment_status = #{status}, callback_time = #{paymentTime}, callback_content = #{callbackContent}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE payment_info
SET payment_status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE payment_info
SET transaction_id = #{thirdPartyPaymentNo}, payment_status = #{status}, callback_content = #{callbackContent}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT

FROM payment_info
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM payment_info
WHERE user_id = #{userId}
